'use strict';

const assert = require('assert');

const { countSubstrings } = require('./');

describe('#countSubstrings', () => {
  const tests = [
    {
      s: 'abc',
      result: 3,
    },
    {
      s: "",
      result: 1664,
    },
  ];

  for (const { s, result } of tests) {
    it(`${s} -> ${result}`, () => {
      assert.deepStrictEqual(countSubstrings(s), result);
    });
  }
});
